  <script>
    // åœ–åƒè³‡æºåˆ—è¡¨
    const imageUrls = [
      "https://raw.githubusercontent.com/LimeBlogs/Sudachi-Res/refs/heads/main/bg1.png",
      "https://raw.githubusercontent.com/LimeBlogs/Sudachi-Res/refs/heads/main/bg2.png",
      "https://raw.githubusercontent.com/LimeBlogs/Sudachi-Res/refs/heads/main/bg3.png"
    ];

    // å¾ <three> æ¨™ç±¤è§£ææ¨™é¡Œã€ä»‹ç´¹å’Œå›é¥‹
    const dataContainer = document.querySelector('three');
    const textDataLines = dataContainer.textContent.trim().split('\n');

    // å°‡åœ–åƒURLå’Œæ–‡æœ¬æ•¸æ“šåˆä½µæˆä¸€å€‹å®Œæ•´çš„èƒŒæ™¯æ•¸æ“šé™£åˆ—
    const backgrounds = textDataLines.map((line, index) => {
      const [title, desc, feedback] = line.trim().split('|');
      return {
        url: imageUrls[index],
        title: title,
        desc: desc,
        feedback: feedback
      };
    });

    // ç•¶å‰èƒŒæ™¯ç´¢å¼•
    let currentBgIndex = 0;

    // ç²å–DOMå…ƒç´ 
    const body = document.body;
    const prevBtn = document.getElementById('prevBtn');
    const nextBtn = document.getElementById('nextBtn');
    const infoPanel = document.getElementById('infoPanel');
    const bgTitle = document.getElementById('bgTitle');
    const bgDesc = document.getElementById('bgDesc');
    const thankYou = document.getElementById('thankYou');
    const currentTitle = document.getElementById('currentTitle');
    const closeBtn = document.getElementById('closeBtn');

    // åˆå§‹åŒ–èƒŒæ™¯
    function initBackground() {
      if (backgrounds.length > 0) {
        body.style.backgroundImage = `url(${backgrounds[currentBgIndex].url})`;
        bgTitle.textContent = backgrounds[currentBgIndex].title;
        bgDesc.textContent = backgrounds[currentBgIndex].desc;
      }
    }

    // åˆ‡æ›èƒŒæ™¯å‡½æ•¸
    function switchBackground(newIndex) {
      // æ·¡å‡ºæ•ˆæœ
      infoPanel.style.opacity = 0;

      setTimeout(() => {
        currentBgIndex = newIndex;
        body.style.backgroundImage = `url(${backgrounds[currentBgIndex].url})`;
        bgTitle.textContent = backgrounds[currentBgIndex].title;
        bgDesc.textContent = backgrounds[currentBgIndex].desc;

        // æ·¡å…¥æ•ˆæœ
        infoPanel.style.opacity = 1;
      }, 250);
    }

    // ä¸Šä¸€å€‹èƒŒæ™¯
    function showPrevBackground(event) {
      // é˜»æ­¢äº‹ä»¶å†’æ³¡ï¼Œé¿å…è§¸ç™¼bodyçš„é»æ“Šäº‹ä»¶
      event.stopPropagation();
      let newIndex = currentBgIndex - 1;
      if (newIndex < 0) {
        newIndex = backgrounds.length - 1;
      }
      switchBackground(newIndex);
    }

    // ä¸‹ä¸€å€‹èƒŒæ™¯
    function showNextBackground(event) {
      // é˜»æ­¢äº‹ä»¶å†’æ³¡ï¼Œé¿å…è§¸ç™¼bodyçš„é»æ“Šäº‹ä»¶
      event.stopPropagation();
      let newIndex = currentBgIndex + 1;
      if (newIndex >= backgrounds.length) {
        newIndex = 0;
      }
      switchBackground(newIndex);
    }

    // é»æ“ŠèƒŒæ™¯é¡¯ç¤ºæ„Ÿè¬æ–‡æœ¬
    function showThankYou() {
      // è¨­ç½®ç•¶å‰å¡ç‰‡æ¨™é¡Œ
      currentTitle.textContent = "å·²é¸æ“‡æ—…è¡Œ " + backgrounds[currentBgIndex].title;
      thankYou.classList.add('show');
    }

    // éš±è—æ„Ÿè¬æ–‡æœ¬ï¼Œå›åˆ°èƒŒæ™¯é è¦½
    function hideThankYou() {
      thankYou.classList.remove('show');
    }

    // é›™æ“Šç™¼é€å›é¥‹
    function handleDoubleClick() {
      const currentFeedback = backgrounds[currentBgIndex].feedback;

      // èª¿ç”¨ç™¼é€å›é¥‹çš„å‡½æ•¸
      if (typeof triggerSlash === 'function') {
        triggerSlash(`/send ${currentFeedback}`);
        triggerSlash(`/trigger`);
        // ç§»é™¤å°è©±æ¡†
        document.querySelector('.thank-you').remove();
      } else {
        console.log("SillyTavern API 'triggerSlash' not found. Action: /send " + currentFeedback);
      }
    }

    // ç¶å®šæŒ‰éˆ•äº‹ä»¶
    prevBtn.addEventListener('click', showPrevBackground);
    nextBtn.addEventListener('click', showNextBackground);
    body.addEventListener('click', showThankYou);
    closeBtn.addEventListener('click', function(event) {
      event.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡åˆ°body
      hideThankYou();
    });

    // é›™æ“Šäº‹ä»¶ç¶å®šåˆ°æ„Ÿè¬æŸ¥çœ‹ç•Œé¢
    thankYou.addEventListener('dblclick', handleDoubleClick);

    // åˆå§‹åŒ–
    initBackground();
  </script>
</body>
