      const emojis = ['ğŸ§', 'â¤', 'ğŸ”', 'ğŸ‘š', 'ğŸ©³', 'ğŸ©²', 'ğŸ©´', 'ğŸ™', 'ğŸ“…', 'ğŸ•—', 'ğŸ“', 'ğŸ’­', 'ğŸ°', 'ğŸ¤¢', 'ğŸ‘¥', 'ğŸ‘©â€ğŸ¨', 'ğŸ’‰', 'ğŸ“', 'âš–ï¸', 'â™Œ'];

      // è™•ç†æ¯è¡Œæ•¸æ“šä¸¦æ·»åŠ å‹•ç•«å»¶é²
      lines.forEach((line, index) => {
